class list{
private:
	node *head, *tail;
public:
	list(){
		head=NULL;
		tail=NULL;
}};

void createnode(int value){
 	node *temp=new node;
 	temp->data=value;
 	temp->next=NULL;
 	if(head==NULL){
 		head=temp;
 		tail=temp;
 		temp=NULL;
 	}
 	else{
 		tail->next=temp;
 		tail=temp;
 	}
}

void insert_position(int pos, int value){
 	node *pre=new node;
 	node *cur=new node;
 	node *temp=new node;
 	cur=head;
 	for(int i=1;i<pos;i++){
 		pre=cur;
 		cur=cur->next;
 	}
 	temp->data=value;
 	pre->next=temp;
 	temp->next=cur;
}

void delete_position(int pos){
 	node *current=new node;
 	node *previous=new node;
 	current=head;
 	for(int i=1;i<pos;i++){
 		previous=current;
 		current=current->next;
 	}
 	previous->next=current->next;
}

