 	if(head==NULL){
 		head=temp;
 		tail=temp;
 		temp=NULL;
 	}
 	else{
 		tail->next=temp;
 		temp->prev=tail;
 		tail=temp;
 	}
}

void insert_position(int pos, int value){
 	node *pre=new node;
